mapper.update:
		update TBL_USER set sessionkey= #{sessionkey}
                ,sessionlimit = sysdate + 7
		where id = #{id}

mapper.select:
		select * from TBL_USER 
				where sessionkey= #{sessionkey} 
                and sessionlimit > sysdate AND enabled != '0'

mapper.update:
update TBL_USER
set id=#{id}, name=#{name}, email=#{email}, jip=#{jip}, address=#{address}, dtaddress=#{dtaddress}
where uno=#{uno}

update TBL_USER
set id=#{id}, name=#{name}, email=#{email}, tel=#{tel}, enabled=#{enabled}, jip=#{jip}, address=#{address}, dtaddress=#{dtaddress}
where uno=#{uno}

UPDATE TBL_USER
SET enabled = '0'
WHERE uno=#{uno}
